mapper.update:
update role
set title = #{title,jdbcType=VARCHAR},
  create_date = #{createDate,jdbcType=TIMESTAMP},
  last_update_date = #{lastUpdateDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, title, create_date, last_update_date
from role
where id = #{id,jdbcType=INTEGER}
